ruleBookInfo.init:
@js:
(function(){
    let doc = org.jsoup.Jsoup.parse(result)
    let og = (selector) => doc.select('[property="og:' + selector + '"]').attr('content')
    return {
        name: og('novel:book_name'),
        author: og('novel:author'),
        kind: og('novel:category'),
        latest: og('novel:latest_chapter_name')+' · '+String(doc.select('.top span:nth-child(4)').textNodes().get(0)),
        cover: og('image'),
        wordCount: String(doc.select('.top span:nth-child(2)').text()).replace(/全文字数：/,'').replace(/万字/,'000'),
        intro: og('description'),
        url:og('novel:read_url')
    }
})()

