style:
/*消除链接下划线*/
a{text-decoration: none;}


/*去除details小三角及边框*/
details summary::-webkit-details-marker { display:none; }
summary{outline:none;}


/*div样式〔渐变〕*/
.渐变{border: 1px solid; border-image: -webkit-linear-gradient(90deg,#6772FF, #51E2F7) 5 10;}


/*div样式〔按钮〕*/
.按钮{
/*背景颜色和字体颜色*/
background:#f2ecde; color:#622a1d;
/*边框大小及颜色(上，左，右，下)*/
border-top:2px   solid #dcdcdc;
border-left:2px   solid #dcdcdc;
border-right:2px   solid #a3a3a3;
border-bottom:2px   solid #a3a3a3;
/*阴影大小及颜色(下，右下，右)*/
box-shadow: 1px 0px 1px #404040;
/*文字与边框的距离(左，右)*/
padding-left:2px;
padding-right:2px;
/*圆角弧度*/
border-radius:10px;
/*宽与高*/
width:90%;
height:25px;
/*文字居中*/
text-align: center;
}


/*div样式〔背景〕*/
.背景{
/*背景颜色*/
background-color:#f2ecde;
/*圆角弧度*/
border-radius:15px;
/*边框大小及颜色*/
border:2px   solid #622a1d;
}



/*订阅复刻相关*/

/*搜索*/
.搜索1{width:80%; text-indent:10px; border-top-left-radius:10px; border-bottom-left-radius:10px}
.搜索2{width:20%; border-left:0; color:000; border-top-right-radius:10px; border-bottom-right-radius:10px}


/*分类*/
.分类框架{width:98%; height:25px; display:-webkit-box; text-align:center; overflow:auto; border:1px solid #000; border-radius:5px;}
.分类框架 li{width:20%; height:100%}

.当前分类{background:#080808; color:#c4d77e; border-radius:4px;}


/*内容*/
.正文{height:67%; border-top:1px solid #000; background:#fff; position:absolute; z-index:2}
.内容{display:none; overflow:auto; height:100%}
.默认显示{display:block}
.内容 li{float:left; height:19.5%; border-bottom:1px solid #000}

.信息{width:70%}
.封面{width:30%}
.信息 h3{height:70%}
.信息 h5{height:30%;overflow:auto}
.封面 img{height:100%}

